INSERT INTO `products` VALUES ('12', 'rbd-coconut-oil', 'RBD COCONUT OIL', 'RBD COCONUT OIL', 'RBD', 'RBD  ', '  ', 'For Crude Coconut Oil to be suitable for consumption, it must be refined, bleached and\n\ndeodorized (RBD). RBD Coconut Oil is a light yellow liquid and would change into a semi solid\n\nin room temperature. RBD Coconut Oil is used in coconut-based cooking oil, for food processing\n\ncommercial and in oleo chemical industries.', 'For Crude Coconut Oil to be suitable for consumption, it must be refined, bleached and\n\ndeodorized (RBD). RBD Coconut Oil is a light yellow liquid and would change into a semi solid\n\nin room temperature. RBD Coconut Oil is used in coconut-based cooking oil, for food processing\n\ncommercial and in oleo chemical industries.', 'RBDRBDRBD', '      ,      (RBD). RBD         . RBD         ,    .', '           ,    ,    ().       - ,       .             ,        - .', 'SMP-product-rbdcno.jpg', '0000-00-00 00:00:00', '0000-00-00 00:00:00');
INSERT INTO `products` VALUES ('13', 'refined-bleached-and-deodorized-rbd-palm-stearin', 'REFINED, BLEACHED AND DEODORIZED (RBD) PALM STEARIN', 'REFINED, BLEACHED AND DEODORIZED (RBD) PALM STEARIN', 'RBD', ', , (RBD) ', ',    ()  ', 'RBD Palm stearin is the solid product obtained from the fractionation of palm oil. The other\n\nproduct is in liquid form, called RBD Palm Olein. RBD Palm Stearin is white in color and solid\n\nat room temperature. RBD Palm Stearin is used in the production of margarine and shortenings as\n\nwell as to make soaps, candles and also used in oleo chemical industries.', 'RBD Palm stearin is the solid product obtained from the fractionation of palm oil. The other\n\nproduct is in liquid form, called RBD Palm Olein. RBD Palm Stearin is white in color and solid\n\nat room temperature. RBD Palm Stearin is used in the production of margarine and shortenings as\n\nwell as to make soaps, candles and also used in oleo chemical industries.', 'RBDRBDRBDRBD', 'RBD      .  RBD     . RBD     .           ,    .', '      ,     .           .              , ,   - .', 'SMP-product-stearine1.jpg', '0000-00-00 00:00:00', '0000-00-00 00:00:00');
INSERT INTO `products` VALUES ('14', 'palm-fatty-acid-distillate-pfad', 'PALM FATTY ACID DISTILLATE (PFAD)', 'PALM FATTY ACID DISTILLATE (PFAD)', 'PFAD', '   (PFAD)', '     ()', 'Palm Fatty Acid distillate is the by-product obtained during the refining of crude palm oil. It is solid and light brown in color at room temperature. It is used mainly in the production of soaps, detergents and a few in the cosmetic industries.', 'Palm Fatty Acid distillate is the by-product obtained during the refining of crude palm oil. It is solid and light brown in color at room temperature. It is used mainly in the production of soaps, detergents and a few in the cosmetic industries.', '', '       .      .   , ,   .', '     ()    ,       .   -    .       ,       .', 'SMP-product-pfad.jpg', '0000-00-00 00:00:00', '0000-00-00 00:00:00');
INSERT INTO `products` VALUES ('15', 'refined-bleached-deodorized-palm-olein-rbd-palm-olein', 'REFINED, BLEACHED, DEODORIZED PALM OLEIN (RBD PALM OLEIN)', 'REFINED, BLEACHED, DEODORIZED PALM OLEIN (RBD PALM OLEIN)', 'RBD', ', ,   ', ',    ()  ', 'RBD Palm Olein is the liquid product obtained from the fractionation of palm oil. The other product is called RBD Palm Stearin, and is in a solid state. RBD Palm Olein is a clear yellow liquid at room temperature. RBD Palm Olein is used as cooking oil and also in food preparation commercials. It is also used in oleo chemical industries as well as to make biodiesel and other cosmetic products.', 'RBD Palm Olein is the liquid product obtained from the fractionation of palm oil. The other product is called RBD Palm Stearin, and is in a solid state. RBD Palm Olein is a clear yellow liquid at room temperature. RBD Palm Olein is used as cooking oil and also in food preparation commercials. It is also used in oleo chemical industries as well as to make biodiesel and other cosmetic products.', 'RBDRBDRBD', 'RBD         .    RBD       .     . RBD       .              .', '    ,     .      ,     .    -       .        ,       .    - ,    ,  .', 'SMP-product-olein.jpg', '0000-00-00 00:00:00', '0000-00-00 00:00:00');
INSERT INTO `products` VALUES ('16', 'crude-coconut-oil-ccno', 'CRUDE COCONUT OIL (CCNO)', 'CRUDE COCONUT OIL (CCNO)', '(CCNO)', '  ', '   ()', 'Crude Coconut Oil is crushed from copra, the dried meat of finely matured coconut. It must be further processed to be applicable for use in foods, soaps and in the oleo chemical industry.', 'Crude Coconut Oil is crushed from copra, the dried meat of finely matured coconut. It must be further processed to be applicable for use in foods, soaps and in the oleo chemical industry.', '', '     . (:  ).           .', '     ,     .      ,        ,    - .', 'SMP-product-hcno3.jpg', '0000-00-00 00:00:00', '0000-00-00 00:00:00');
INSERT INTO `products` VALUES ('17', 'hydrogenated-coconut-oil-hcno', 'HYDROGENATED COCONUT OIL (HCNO)', 'HYDROGENATED COCONUT OIL (HCNO)', 'HCNO', '    (HCNO)', '   ()', 'HCNO is RBD CNO that went through a Hydrogenation process. HCNO is a solid white fat in texture and a clear liquid if applied to heat. HCNO can be used as the main raw material in food industries.', 'HCNO is RBD CNO that went through a Hydrogenation process. HCNO is a solid white fat in texture and a clear liquid if applied to heat. HCNO can be used as the main raw material in food industries.', 'HCNORBDHCNOHCNO', 'HCNO    RBD CNO . HCNO       .        .', ' -   ,    .        ,       .          .', 'SMP-product-hcno.jpg', '0000-00-00 00:00:00', '0000-00-00 00:00:00');
INSERT INTO `products` VALUES ('18', 'coconut-fatty-acid-distillate-cfad', 'COCONUT FATTY ACID DISTILLATE (CFAD)', 'COCONUT FATTY ACID DISTILLATE (CFAD)', 'PFAD', '  (CFAD)', '     ()', 'Coconut Fatty Acid Distillate is the side product obtained during the refining process of crude coconut oil. It is a reddish brown liquid at room temperature and is mainly used in soap manufacturing industries.', 'Coconut Fatty Acid Distillate is the side product obtained during the refining process of crude coconut oil. It is a reddish brown liquid at room temperature and is mainly used in soap manufacturing industries.', '', '       .         .', '        ,       .  -           .', 'SMP-product-cfad.jpg', '0000-00-00 00:00:00', '0000-00-00 00:00:00');
